const m = require('./index.js');

console.log(m.getJSON({foo: {bar: 'baz'}}, 'foo.bar')) // => 'baz'

console.log(m.setJSON({foo: {bar: 'baz'}}, 'foo.bar', 'jazz')) // => {foo: {bar: 'jazz'}}

console.log(m.setJSON({foo: {bar: 'baz'}}, 'foo.bar', 'value', {
  guess: true
})) // => {foo: {bar: true}}

console.log(m.setJSON({foo: {bar: 'baz', something: null}}, 'foo.bar', 'value', {
  prune: true // Remove nulls and prune objects
})) // => {foo: {bar: 'value'}}

console.log(m.from({foo: {bar: 'baz'}})
 .set('foo.bar', 'baz'));

console.log('z', m.from({foo: {bar: 'baz'}})
 .get());
