declare module Zlib {

/**
 * 任意の number[], Uint8Array を ZLib コンテナで圧縮するクラス.
 */
export class Deflate {
  /**
   * @param input Input buffer.
   * @param option Deflate options.
   */
  constructor(input: Uint8Array, option?: DeflateOption);
  /**
   * @param input Input buffer.
   * @param option Deflate options.
   */
  constructor(input: number[], option?: DeflateOption);
  /**
   * 圧縮を行う.
   * @returns TypedArray をサポートしているブラウザでは Uint8Array,
   *   それ以外の環境では number[] を返す.
   */
  compress(): any;
  static CompressionType: DeflateCompressionType;
}

enum DeflateCompressionType {
  NONE,
  FIXED,
  DYNAMIC,
}

interface DeflateOption {
  compressionType?: DeflateCompressionType;
  lazy?: number;
  outputBuffer?: Uint8Array;
  outputIndex?: number;
}

}
