declare module Zlib {

/**
 * GZIP コンテナで圧縮された number[], Uint8Array を 伸張 するクラス.
 */
export class Gunzip {
  /**
   * @param input Input buffer.
   * @param option Gzip options.
   */
  constructor(input: Uint8Array, option?: GzipOption);
  /**
   * @param input Input buffer.
   * @param option Gzip options.
   */
  constructor(input: number[], option?: GzipOption);
  /**
   * 全メンバーの伸張を行う.
   * @returns TypedArray をサポートしているブラウザでは Uint8Array,
   *   それ以外の環境では number[] を返す.
   */
  decompress(): any;
  /**
   * メンバーを結合せずに返す.
   */
  getMembers(): GzipMember[];
}

interface GzipOption {
  flags?: {
    fname: bool;
    comment: bool;
    fhcrc: bool;
  };
  filename?: string;
  comment?: string;
}

class GzipMember {
  /**
   * メンバの名前を取得する.
   */
    getName(): string;

  /**
   * メンバーの伸張後のデータを取得する.
   * @returns TypedArray をサポートしているブラウザでは Uint8Array,
   *   それ以外の環境では number[] を返す.
   */
    getData(): any;

  /**
   * メンバーの更新日時を取得する.
   */
    getMtime(): Date;
}

}
