declare module Zlib {

/**
 * ZLIB 圧縮された任意の number[], Uint8Array を伸張するクラス.
 */
export class Inflate {
  /**
   * @param input Input buffer.
   * @param option Inflate options.
   */
  constructor(input: Uint8Array, option?: InflateOption);
  /**
   * @param input Input buffer.
   * @param option Inflate options.
   */
  constructor(input: number[], option?: InflateOption);
  /**
   * 伸張を行う.
   * @returns TypedArray をサポートしているブラウザでは Uint8Array,
   *   それ以外の環境では number[] を返す.
   */
  decompress(): any;
  static BufferType: InflateBufferType;
}

enum InflateBufferType {
  BLOCK,
  ADAPTIVE
}

interface InflateOption {
  index?: number;
  blockSize?: number;
  verify?: bool;
  bufferType?: InflateBufferType;
}

}



